   * 数组排序
   * @param {*} attr 对象属性
   * @param {*} rev 默认升序，true升序，false降序
   * arr.sort(compare('age',false))
   */
  compare (attr, rev) {
    if (rev === undefined) {
      rev = 1
    } else {
      rev = (rev) ? 1 : -1
    }
    return function (a, b) {
      a = a[attr]
      b = b[attr]
      if (a < b) {
        return rev * -1
      }
      if (a > b) {
        return rev * 1
      }
      return 0
    }
  }
