import utils from '@/common/utils'
import common from '@/mixins/common'

const RESOURCE_PATH = '/udsSystem'

export default {
  props: {
    transData: {
      type: Object,
      default: function () {
    			return {}
      }
    }
  },
  mixins: [ common ],
  data: function () {
    return {
      formBean: {},
      formBean3: {},
      formBean2: {},
      dbControlList: [],
      dbControlName: {},
      allStop: {
        show: false
      },
      allStart: {
        show: false
      },
      loadingStatus: false,
      loadingStatus2: false,
      loadingdel: false,
      request: {
        delReq: {
          method: 'DELETE',
          url: RESOURCE_PATH
        },
        addReq: {
          method: 'POST',
          url: RESOURCE_PATH
        }
      },
      statusList: [
        {
          value: '1',
          label: '执行节点并发配置'
        },
        {
          value: '0',
          label: '调度系统并发配置'
        }

      ],
      strategyList: [],
      gridColumns: [
        {
          type: 'selection',
          width: 60,
          align: 'center'
        },
        {
          title: '平台名',
          key: 'platform',
          minWidth: 100
        },
        {
          title: '应用名',
          key: 'systems',
          minWidth: 100
        },
        {
          title: '逻辑集群',
          key: 'serverRoleNameGroup',
          minWidth: 100
          // render: (h, { column, index, row }) => {
          //   return h('div', [
          //     h('Span', {}, this.dbControlName[row.serverRoleNameGroup])
          //   ])
          // }
        },
        // {
        // 	title: '脚本信号文件保存天数',
        // 	key: 'dataKeepDay',
        // },
        // {
        // 	title: '脚本日志保存天数',
        // 	key: 'logKeepDay',
        // },
        // {
        // 	title: '记录保存天数',
        // 	key: 'recordKeepDay',
        // },

        // {
        //   title: '该平台最大运行作业数',
        //   key: 'sumMax',
        //   width: 160,
        //   align: 'center'
        // },
        {
          title: '最大运行作业数',
          key: 'maxRunJob',
          align: 'center',
          minWidth: 110
        },
        {
          title: '分发策略',
          key: 'select',
          align: 'center',
          minWidth: 150,
          render: (h, params) => {
            let strategy = params.row.select
            let str = ''
            this.strategyList.forEach(data1 => {
              if (data1.value == strategy) {
                str = data1.label
              }
            })
            if (str == '') {
              str = strategy
            }
            return h('div', {
              props: {
              }
            }, str)
          }
        },
        {
          title: '策略参数',
          key: 'selectPro',
          // width: 200,
          minWidth: 200,
          // 超出长度省略符号
          render: (h, params) => {
            let str = params.row.selectPro
            let str1 = ''
            let str0 = ''
            while (str.length > 30) {
              str0 = str.substring(0, 30) + '\n'
              str = str.substring(30, str.length)
              str1 = str1 + str0
            }

            return h('div', [
              h('span', {
                style: {
                  display: 'inline-block',
                  width: '100%',
                  overflow: 'hidden',
                  textOverflow: 'ellipsis',
                  whiteSpace: 'nowrap'// 'nowrap'
                },
                // 鼠标悬停 显示完整信息
                domProps: {
                  title: str1 // params.row.strategyPro,

                },
                on: {
                  click: (e) => {
                    e.stopPropagation()
                  }
                }
              }, params.row.selectPro)
            ])
          }
        },
        {
          title: '是否启用平台数据',
          key: 'usePlatformMsg',
          align: 'center',
          minWidth: 110
        },
        {
          title: '描述',
          key: 'des',
          minWidth: 150
        },
        {
          title: '操作',
          align: 'center',
          width: 100,
          // fixed: 'right',
          render: (h, { column, index, row }) => {
            return h('div', [
              h('Button', {
                props: {
                  size: 'small',
                  type: 'info'
                },
                style: {
                  marginRight: '5px'
                },
                on: {
                  click: () => {
                    let platform = row.platform
                    let sys = row.systems
                    let dbControl = row.serverRoleNameGroup
                    let queryCache = { formBean: this.formBean, currentPage: this.page.current, pageSize: this.page.size }
                    this.$emit('switch', Object.assign({ platform: platform, sys: sys, dbControl: dbControl, data: row }, queryCache)) // 提交form事件，在parent中显示form
                  }
                }
              }, '编辑')
            ])
          }
        }
      ],
      gridData: [],
      systemData: [],
      platformData: [],
      platformData2: [],
      platformData3: [],
      selection: [],
      page: {
        current: 1,
        total: 10,
        size: 10,
        opt: [10, 50, 100]
      },
      ruleValidate: {
      }
    }
  },
  methods: {
    paramChange () {
      if (this.formBean.param == 1) {
        this.$emit('switch', { mtable: 'true' })
      }
    },
    queryPlatform () {
      let loadConfig = {
        method: 'GET',
        url: '/msys/getPlatformList'
      }
      this.$ajax(loadConfig)
        .then(resp => {
          resp.data.forEach(data => {
            let tmp = {}
            tmp.value = data
            tmp.label = data
            this.platformData.push(tmp)
            this.platformData2.push(tmp)
            this.platformData3.push(tmp)
          })
        })
    },
    querySystem () {
      let loadConfig = {
        method: 'GET',
        url: '/msys/getSysList',
        params: { platform: this.formBean.platform }
      }

      // modify by jcjin 20200529 for systemData返回值调整为key-value
      // this.$ajax(loadConfig)
      // .then(resp => {
      //	this.formBean.system = "";
      //	this.systemData = resp.data;
      //
      // })
      this.systemData = []
      this.$refs.refsystem.setQuery('')
      if (this.formBean.platform) {
        this.$ajax(loadConfig)
          .then(resp => {
            resp.data.forEach(data => {
              let tmp = {}
              tmp.value = data
              tmp.label = data
              this.systemData.push(tmp)
            })
          })
      }
    },
    filterMethod (value, option) {
      return option.toUpperCase().indexOf(value.toUpperCase()) !== -1
    },
    /**
		 * 初始化
		 **/
    init () {
      if (this.transData.currentPage) {
        this.formBean = this.transData.formBean
        this.page.current = this.transData.currentPage
        this.page.size = this.transData.pageSize
      }
      // this.queryDbControlList()
      this.search()
      this.getPlatformList()
      // this.queryPlatform()
    },
    /**
     * 如果传过来的有初始数据则进行数据绑定
     **/
    bindData (fields) {
      try {
        const data = Object.assign({}, this.transData.initFormBean)
        if (fields) { // 进行可选字段初始化
          for (const field of fields) {
            this.formBean[field] = data[field]
          }
        } else { // 进行全量字段初始化（默认是只有主表的主键的）
          this.formBean = Object.assign({}, data)
        }
      } catch (error) {
        console.error(error)
      }
    },
    /**
		 * 下载
		 **/
    download () {
      let params = {}
      Object.assign(params, this.formBean)
      params.type = 'excel'
      utils.download(RESOURCE_PATH + '/downLoad', params)
    },
    queryDbControlList () {
      let loadConfig = {
        method: 'GET',
        url: '/dictionary',
        params: { dicCode: 'db_control' }
      }
      this.$ajax(loadConfig).then(resp => {
        this.dbControlList = []
        resp.data.rows.forEach(data => {
          this.dbControlName[parseInt(data.dicValue)] = data.dicName
          let tmp = {}
          tmp.value = parseInt(data.dicValue)
          tmp.label = data.dicName
          this.dbControlList.push(tmp)
        })
      })
    },
    /**
		 * 查询
		 **/
    search () {
      this.selection = []
      let params = {}
      Object.assign(params, this.formBean)
      // params.currentPage = this.page.current
      // params.pageSize = this.page.size
      params.current = this.page.current
      params.size = this.page.size

      let httpConfig = {
        method: 'GET',
        url: RESOURCE_PATH + '/selectAll',
        params: params
      }
      this.$ajax(httpConfig)
        .then(resp => {
          this.loading = false
          this.gridData = resp.data.rows
          this.page.total = resp.data.total
          resp.data.rows.forEach(data1 => {
            if (data1.usePlatform) {
              data1.usePlatform = '1'
              data1.usePlatformMsg = '启用'
            } else {
              data1.usePlatform = '0'
              data1.usePlatformMsg = '禁用'
            }
            // if (data1.strategy == 0) {
            //   data1.strategy = '普通策略'
            // } else {
            //   // alert(data1.strategy);
            //   data1.strategy = '指定机器分发机器'
            // }

            data1.strategy = data1.select + ''
          })
        })
    },
    demand () {
      this.page.current = 1
      this.search()
    },
    allStartOpen () {
      this.allStart.show = true
    },
    allStopOpen () {
      this.allStop.show = true
    },
    allstop () {
      // this.$refs.allStopForm.validate(valid => {
      // if(!valid) return;
      if (!this.formBean3.platform) {
        this.$Message.warning('请选择平台')
        return
      }
      let params = {}
      Object.assign(params, this.formBean3)
      params.authps = this.formBean3.platform + '*'
      let httpConfig = {
        url: RESOURCE_PATH,
        data: params
      }
      if (this.formBean3.platform) {
        httpConfig.method = 'PUT'
        httpConfig.url = RESOURCE_PATH + '/allStop'
      }
      this.loadingStatus2 = true
      this.$ajax(httpConfig)
        .then(resp => {
          this.loadingStatus2 = false
          // let str=resp.data;
          if (resp.status && resp.status == 200) {
            this.allStop.show = false
            this.search()
          }
        })
      // });
    },
    allstart () {
      // if(!this.formBean2.maxRunJob){
      // 	this.$Message.warning("请输入默认值");
      // 	return ;
      // }
      if (!this.formBean2.platform) {
        this.$Message.warning('请选择平台')
        return
      }
      let params = {}
      Object.assign(params, this.formBean2)
      params.authps = this.formBean2.platform + '*'
      let httpConfig = {
        url: RESOURCE_PATH,
        data: params
      }
      if (this.formBean2.platform) {
        httpConfig.method = 'PUT'
        httpConfig.url = RESOURCE_PATH + '/allStart'
      }
      this.loadingStatus = true
      this.$ajax(httpConfig)
        .then(resp => {
          this.loadingStatus = false
          // let str=resp.data;
          if (resp.status && resp.status == 200) {
            this.allStart.show = false
            this.search()
          }
        })
    },

    /**
		 * 清空
		 **/
    clear () {
      this.formBean = {}
      this.selection = []
      this.page = {
        current: 1,
        size: 10
      }
    },

    /**
		 * 新增
		 **/
    add () {
      this.$emit('switch', null)
    },

    /**
		 * 改变页码事件
		 **/
    changePage (currentPage) {
      this.page.current = currentPage
      this.search()
    },
    /**
		 * 改变分页大小事件
		 **/
    changePageSize (pageSize) {
      this.page.size = pageSize
      this.search()
    },
    // harrier getPlatformList
    getPlatformList () {
      let loadConfig = {
        method: 'GET',
        url: '/udsSystem/listQuery'
      }
      this.$ajax(loadConfig)
        .then(resp => {
          let platformList = []
          resp.data.forEach(data => {
            platformList.push(data.platform)
            // this.formBean.platform = "UDS";
          })
          platformList = Array.from(new Set(platformList))
          this.platformData = []
          this.platformData2 = []
          this.platformData3 = []
          platformList.forEach(data => {
            let tmp = {}
            tmp.value = data
            tmp.label = data
            this.platformData.push(tmp)
            this.platformData2.push(tmp)
            this.platformData3.push(tmp)
            // this.formBean.platform = "UDS";
          })
          this.search()
        })
    },
    // harrier getSystemList
    getSystemList () {
      let loadConfig = {
        method: 'GET',
        url: '/udsSystem/listQuery'
      }
      this.systemData = []
      this.$refs.refsystem.setQuery('')
      if (this.formBean.platform) {
        this.$ajax(loadConfig)
          .then(resp => {
            resp.data.forEach(data => {
              if (this.formBean.platform === data.platform && data.systems !== '*') {
                let tmp = {}
                tmp.value = data.systems
                tmp.label = data.systems
                this.systemData.push(tmp)
              }
            })
          })
      }
    },
    // harrier test end
    querySelectType () {
      this.strategyList = []
      let params = { dicCode: 'selectType' }
      const loadConfig = {
        method: 'GET',
        url: '/mDictionary/selectDicCode',
        params: params
      }
      this.$ajax(loadConfig)
        .then(resp => {
          if (resp.data) {
            resp.data.forEach(data1 => {
              let temp = {}
              temp.key = data1.dicValue
              temp.value = parseInt(data1.dicKey)
              temp.label = data1.dicValue
              this.strategyList.push(temp)
            })
          }
        })
    },
    /**
     * 删除
     **/
    del: function () {
      const ids = Array.from(this.selection, e => e.id)
      let deleteConfig = {
        method: 'DELETE',
        url: `/udsSystem/${ids}/delete`,
        data: {}
      }
      this.loadingdel = true
      this.$ajax(deleteConfig)
        .then(resp => {
          this.loadingdel = false
          this.search()
        })
    }
  },
  /**
	 * 视图挂载
	 **/
  mounted () {
    this.querySelectType()
    // this.init()
  }
}
