import utils from '@/common/utils'
import common from '@/mixins/common'
import store from '@/store/index'
import SAuthButton from '_c/s-auth-button'
const RESOURCE_PATH = '/udsJobWeight'

export default {
  components: {
    SAuthButton
  },
  props: {
    transData: {
      type: Object,
      default: function () {
    			return {}
      }
    }
  },
  mixins: [ common ],
  data: function () {
    return {
      formBean: {},
      // JobData:[],
      delLoding: false,
      request: {
        delReq: {
          method: 'DELETE',
          url: RESOURCE_PATH
        },
        addReq: {
          method: 'POST',
          url: RESOURCE_PATH
        }
      },
      gridColumns: [
        // {
        // 	type: 'index',
        // 	title: ' ',
        // 	width: 80,
        // 	align: 'center',
        // 	fixed: 'left'
        // },
        {
          type: 'selection',
          width: 60,
          align: 'center',
          fixed: 'left'
        },
        {
          title: '平台',
          width: 100,
          key: 'platform'
        },
        {
          title: '应用',
          width: 100,
          key: 'systems'
        },
        {
          title: '作业名',
          key: 'job',
          minWidth: 180
        },
        {
          title: '类型',
          width: 120,
          key: 'limitType',
          render: (h, params) => {
            let limitType = params.row.limitType
            let str = ''
            this.limitTypeData.forEach(data1 => {
              if (data1.value == limitType) {
                str = data1.key
              }
            })
            if (str == '') {
              str = params.row.limitType
            }
            return h('div', {
              props: {
              }
            }, str)
          }
        },
        {
          title: '配置的全值',
          width: 120,
          key: 'confValue'
        },
        {
          title: '描述',
          minWidth: 200,
          key: 'desc'
        },
        {
          title: '操作',
          width: 100,
          fixed: 'right',
          render: (h, { column, index, row }) => {
            return h('div', [
              h('Button', {
                props: {
                  size: 'small',
                  type: 'info'
                },
                style: {
                  marginRight: '5px'
                },
                on: {
                  click: () => {
                    let id = row.id
                    // this.$emit('switch', {id: id}) //提交form事件，在parent中显示form
                    let queryCache = { formBean: this.formBean, currentPage: this.page.current, pageSize: this.page.size }
                    this.$emit('switch', Object.assign({}, { id: id }, queryCache)) // 提交form事件，在parent中显示form
                  }
                }
              }, '编辑')
            ])
          }
        }
      ],
      gridData: [],
      importOp: {
        show: false
      },
      files: [],
      loading: false,
      loadingStatus: false,
      limitTypeData: [],
      selection: [],
      systemData: [],
      platformData: [],
      page: {
        current: 1,
        total: 10,
        size: 10,
        opt: [10, 50, 100]
      },
      ruleValidate: {
      }
    }
  },
  methods: {
    /**
		 * 初始化
		 **/
    init () {
      // this.queryLimitType()
      // this.queryPlatform()
      // if (this.transData.statusName==="weightTable") {
      if (this.transData.currentPage) {
        this.formBean = this.transData.formBean
        this.page.current = this.transData.currentPage
        this.page.size = this.transData.pageSize
      }
      this.search()
    },
    /**
         * 如果传过来的有初始数据则进行数据绑定
         **/
    bindData (fields) {
      try {
        const data = Object.assign({}, this.transData.initFormBean)
        if (fields) { // 进行可选字段初始化
          for (const field of fields) {
            this.formBean[field] = data[field]
          }
        } else { // 进行全量字段初始化（默认是只有主表的主键的）
          this.formBean = Object.assign({}, data)
        }
      } catch (error) {
        console.error(error)
      }
    },
    /**
		 * 下载
		 **/
    download () {
      let params = {}
      Object.assign(params, this.formBean)
      params.type = 'excel'
      utils.download(RESOURCE_PATH + '/downLoad', params)
    },
    queryLimitType () {
      this.limitTypeData = []
      let params = { dicCode: 'limitType' }
      const loadConfig = {
        method: 'GET',
        url: '/mDictionary/selectDicCode',
        params: params
      }
      this.$ajax(loadConfig)
        .then(resp => {
          if (resp.data) {
            resp.data.forEach(data1 => {
              let temp = {}
              temp.key = data1.dicValue
              temp.value = parseInt(data1.dicKey)
              temp.label = data1.dicValue
              this.limitTypeData.push(temp)
            })
          }
        })
    },
    demand () {
      this.page.current = 1
      this.search()
    },
    /**
		 * 查询
		 **/
    search () {
      let params = {}
      Object.assign(params, this.formBean)
      params.current = this.page.current
      params.size = this.page.size
      if (params.job) {
        params.job = '%' + params.job.trim().toUpperCase() + '%'
      } else {
        delete params.job
      }
      let httpConfig = {
        method: 'GET',
        url: RESOURCE_PATH + '/selectAll',
        params: params
      }
      this.loading = true
      this.$ajax(httpConfig)
        .then(resp => {
          this.loading = false
          // this.gridData = resp.data.rows
          // this.page.total = resp.data.total
          this.gridData = resp.data.rows
          this.page.total = resp.data.total
        })
    },
    getPlatformList () {
      this.platformData = []
      let platformList = this.$store.getters.getUserPlatform()
      platformList.forEach(data => {
        let tmp = {}
        tmp.value = data
        tmp.label = data
        this.platformData.push(tmp)
      })
    },
    getSystemList () {
      this.systemData = []
      this.$refs.refsystem.setQuery('')
      if (this.formBean.platform) {
        let userSystemList = this.$store.getters.getUserSystem()
        let userSystem = userSystemList[this.formBean.platform]
        userSystem.forEach(data => {
          let tmp = {}
          tmp.value = data
          tmp.label = data
          this.systemData.push(tmp)
        })
      }
    },
    queryPlatform () {
      let loadConfig = {
        method: 'GET',
        url: '/msys/getPlatformList'
      }
      this.$ajax(loadConfig)
        .then(resp => {
          resp.data.forEach(data => {
            let tmp = {}
            tmp.value = data
            tmp.label = data
            this.platformData.push(tmp)
          })
        })
    },
    querySystem () {
      let loadConfig = {
        method: 'GET',
        url: '/msys/getSysList',
        params: { platform: this.formBean.platform }
      }
      this.systemData = []
      this.$refs.refsystem.setQuery('')
      if (this.formBean.platform) {
        this.$ajax(loadConfig)
          .then(resp => {
            resp.data.forEach(data => {
              let tmp = {}
              tmp.value = data
              tmp.label = data
              this.systemData.push(tmp)
            })
          })
      }
    },
    queryJob () {
      let loadConfig = {
        method: 'GET',
        url: RESOURCE_PATH + '/getJobList'
      }
      this.$ajax(loadConfig)
        .then(resp => {
          // resp.data.forEach(data1 => {
          // let tmp = {}
          // tmp.value = data1
          // tmp.label = data1
          // this.JobData.push(tmp)
          // })
        })
    },
    excelDownload () {
      let params = {}
      params.type = 'excel'
      utils.download(RESOURCE_PATH + '/template', params)
    },
    importOpen () {
      this.importOp.show = true
    },
    closeImportOp () {
      this.files = []
      this.importOp.show = false
      this.loadingStatus = false
    },
    handleRemove: function (file) {
      this.files.splice(this.files.findIndex(e => e.name === file.name), 1)
    },
    handleUpload: function (file) {
      if (!this.multiple) {
        if (this.files.length > 0) {
          this.fileError = `不能选择多个的文件`
          return false
        }
      }
      if (this.files.find(e => e.name == file.name)) {
        this.fileError = `${file.name}已存在上传列表，已自动过滤掉！`
        return false
      }
      this.files.push(file)
      return false
    },
    	importExcel () {
      let systems = ''
      let authSystems = store.getters.getSystems
      if (authSystems.indexOf('ROLE_ADMIN') == -1) {
        for (let j = 0; j < authSystems.length; j++) {
          if (authSystems[j].indexOf('*') != -1) {
            systems = authSystems[j]
          }
        }
      }
      var formData = new FormData()
      for (const key in this.formBean) {
        formData.append(key, this.formBean[key])
      }
      if (this.files.length == 0 || this.files == null) {
        this.$Message.warning('请选择导入Excel')
      } else {
        for (const ele of this.files) {
          formData.append('file', ele)
        }
        formData.authps = systems
        this.loadingStatus = true
        this.$ajax.post(RESOURCE_PATH + '/import', formData)
          .then(resp => {
            this.loadingStatus = false
            // this.$Message.warning(resp.data.msg)
            this.files = []
            // this.$Message.info("成功")
            this.importOp.show = false
            this.search()
            this.queryPlatform()
            // alert(resp.data.msg)
          })
      }
    },
    /**
		 * 清空
		 **/
    clear () {
      this.formBean = {}
      this.selection = []
      this.page = {
        current: 1,
        size: 10
      }
      this.search()
    },
    /**
		 * 新增
		 **/
    add () {
      this.$emit('switch', null)
    },
    /**
		 * 删除
		 **/
    del () {
      this.delLoding = true
      const ids = Array.from(this.selection, e => e.id)
      let deleteConfig = {
        method: 'DELETE',
        url: `/udsJobWeight/${ids}/delete`,
        data: {}
      }
      this.$ajax(deleteConfig)
        .then(resp => {
          this.delLoding = false
          this.search()
        })
    },
    /**
		 * 数据复选事件
		 **/
    select (selection) {
      this.selection = selection
    },
    /**
		 * 改变页码事件
		 **/
    changePage (currentPage) {
      this.page.current = currentPage
      this.search()
    },
    /**
		 * 改变分页大小事件
		 **/
    changePageSize (pageSize) {
      this.page.size = pageSize
      this.search()
    }
  },
  /**
	 * 视图挂载
	 **/
  mounted () {
    this.init()
    this.getPlatformList()
    this.queryLimitType()
  }
}
